function [ mse, correct] = evaluate(input, weights, biases, target)    # Get the mean squared error and the correct number of classifications    # given the input and weights and biases for feedforward prediction, and the    # target for testing the prediction    # Get the z and activations    [zs, activations] = feedforward(input, weights, biases);        # the predicted class is the max class predicted by the indices.  See if it    # matches the target index predictions    [_, target_idxs] = max(target);        predictions = activations{end};    [_, prediction_idxs] = max(predictions);        # MSE is the squared sum of the vector lengths / ( 2 * N samples )    mse = sum(sqrt(sum((target - predictions).^2)).^2)/(2 * length(target));    correct = sum(target_idxs == prediction_idxs);endfunction