function output_error = get_output_error(zs, activations, y_batch, cost_function)    # Get the output error for a given cost function.  Cost function options are:    # mse - Mean Square Error    # log-likelihood - Log Likelihood    # cross-entropy - Cross Entropy    activation_error = activations{end} - y_batch;    if (strcmp(cost_function,'mse'))        output_error = activation_error .* sigmoid_prime(zs{end});    elseif (strcmp(cost_function, 'cross-entropy'))        output_error = activation_error;    elseif (strcmp(cost_function, 'log-likelihood'))        output_error = activation_error;    endifendfunction