function weights = initialize_weights (nodeLayers, batchSize)  # create a bias layer with random initialization of weights that connect each  # Layer L with layer L+1  (weights will be L X L+1)  weights = {};  for idx = 2:length(nodeLayers)    layer_1_nodes = nodeLayers(idx - 1);    layer_2_nodes = nodeLayers(idx);    # Create a regula old weight for the sinlge forward pass    stdev = 1 / sqrt(nodeLayers(idx - 1));    weights{idx - 1} = normrnd(0, stdev, layer_1_nodes, layer_2_nodes);  endforendfunction