function transformed_z = transform(z, transformation)    if (strcmp(transformation,'sigmoid'))        transformed_z = 1 ./ (1 + e.^-z);    elseif (strcmp(transformation, 'tanh'))        transformed_z = tanh(z)    elseif (strcmp(transformation, 'softmax'))        transformed_z = z / sum(z, 2)    elseif (strcmp(transformation, 'relu'))        transformed_z = max(0, z)    endif    endfunction